import { useState } from "react";
import ExpenseForm from "./ExpenseForm";

import "../css_files/NewExpenses_css/NewExpense.css";

function NewExpense(props) {

  const [errorMessage, setErrorMessage]=useState("null");

  // function inputValidator(titleValidator, amountValidator,inputData){
  //   if (inputData.amount <= 0) {
  //     console.log("invalid");
  //     amountValidator = false;
  //   }
  //   if (inputData.title.trim().length === 0) {
  //     titleValidator = false;
  //   }
  //   return true;
  // }
  function saveExpenseDataHandler(enteredExpenseData) {
    let titleValidator = true;
    let amountValidator = true;

    if(inputValidator(titleValidator, amountValidator, enteredExpenseData)){
      if (!titleValidator) {
        setErrorMessage({
          title: "Invalid input",
          message: "Enter Valid Name"
        });
      }
      else if (!amountValidator) {
        setErrorMessage({
          title: "Invalid Input",
          message: "Enter Valid amount"
        });
      }
      else if (!(amountValidator && titleValidator)) {
        setErrorMessage({
          title: "Invalid input",
          message: "Please enter valid inputs"
        })
      }
      console.log(errorMessage);
    }
    else{
      const expenseData = {
        ...enteredExpenseData,
        id: Math.random().toString()
      };
      props.onAddExpenses(expenseData);
    }
  }


  return (
    <div className="new-expense">
      <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} />
    </div>
  );
}

export default NewExpense;
