import React, { useState, useEffect } from "react";
import "../css_files/ExpenseFilter.css";

function ExpensesYearFilter(props) {
  const [enteredYear, setEnteredYear] = useState("2021");
  const [yearsDataToInt, setYearsDataToInt] = useState([]);

  console.log("Props>>>>>>>>>", props.yearsData);
  useEffect(() => {
    let yearsToInt = props.yearsData.map((ele) => parseInt(ele, 10));
    yearsToInt = yearsToInt.sort((a, b) => a - b);
    setYearsDataToInt(yearsToInt);
    console.log("useEfect in >>>>>>>>", yearsDataToInt);
  }, [props.yearsData]);


  const [sortedUniqueArray, setSortedUniqueArray] = useState([]);

  useEffect(() => {
    // Sort the array
    const sortedArray = [...props.yourArray].sort((a, b) => a - b);

    // Remove duplicates
    const uniqueArray = sortedArray.filter((value, index, self) => {
      return self.indexOf(value) === index;
    });

    // Update the state with the sorted and unique array
    setSortedUniqueArray(uniqueArray);
  }, [props.yourArray]);
  function dropdownChangeHandler(event) {
    setEnteredYear(event.target.value);
    props.onChangeFilter(event.target.value);
  }

  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select value={enteredYear} onChange={dropdownChangeHandler}>
          {yearsDataToInt.map((ele) => (
            <option key={ele} value={ele}>
              {ele}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
}

export default ExpensesYearFilter;
